import{_ as a,W as e,X as r,a1 as t}from"./framework-ea95e8eb.js";const h={},d=t('<h2 id="简介" tabindex="-1"><a class="header-anchor" href="#简介" aria-hidden="true">#</a> 简介</h2><p><strong>基数排序的基本思想</strong>：将整数按位数切割成不同的数字，然后按每个位数分别比较。</p><p><strong>具体做法</strong>：将所有待比较数值统一为同样的数位长度，数位较短的数前面补零。然后，从最低位开始，依次进行一次排序。这样从最低位排序一直到最高位排序完成以后，数列就变成一个有序序列。</p><h2 id="排序步骤" tabindex="-1"><a class="header-anchor" href="#排序步骤" aria-hidden="true">#</a> 排序步骤</h2><h2 id="演示动画" tabindex="-1"><a class="header-anchor" href="#演示动画" aria-hidden="true">#</a> 演示动画</h2><p><img src="https://cdn.staticaly.com/gh/AlexChen68/OSS@master/blog/advance/基数排序.gif" alt="基数排序动画" loading="lazy"></p><h2 id="代码实现" tabindex="-1"><a class="header-anchor" href="#代码实现" aria-hidden="true">#</a> 代码实现</h2><h2 id="复杂度分析" tabindex="-1"><a class="header-anchor" href="#复杂度分析" aria-hidden="true">#</a> 复杂度分析</h2>',8),n=[d];function i(s,c){return e(),r("div",null,n)}const l=a(h,[["render",i],["__file","5-radix.html.vue"]]);export{l as default};
