import{_ as e,o as a,c as t,Q as l}from"./chunks/framework.01af844e.js";const u=JSON.parse('{"title":"树 - 堆","description":"","frontmatter":{"title":"树 - 堆","date":"2023-05-11T00:00:00.000Z"},"headers":[],"relativePath":"advance/ds/2.5-heap.md","filePath":"advance/ds/2.5-heap.md","lastUpdated":1730355395000}'),i={name:"advance/ds/2.5-heap.md"},o=l('<h2 id="_1-什么是堆" tabindex="-1">1. 什么是堆？ <a class="header-anchor" href="#_1-什么是堆" aria-label="Permalink to &quot;1. 什么是堆？&quot;">​</a></h2><p>堆是一种特殊的树，只要满足下面两个条件，它就是一个堆：</p><ol><li>堆是一颗完全二叉树；</li><li>堆中某个节点的值总是不大于 (或不小于) 其父节点的值。</li></ol><p>其中，我们把根节点最大的堆叫做大顶堆（或最大堆），根节点最小的堆叫做小顶堆（最小堆）。</p><h2 id="总结" tabindex="-1">总结 <a class="header-anchor" href="#总结" aria-label="Permalink to &quot;总结&quot;">​</a></h2><ul><li>堆是一颗完全二叉树；</li><li>小（大）顶堆中的每一个节点都不小于（不大于）它的父节点；</li><li>堆的插入、删除元素的时间复杂度都是 <em>O(log~n~)</em>；</li><li>建堆的时间复杂度是 <em>O(n)</em>；</li><li>堆排序的时间复杂度是 <em>O(nlog~n~)</em>；</li><li>堆排序的空间复杂度是 <em>O(1)</em>；</li></ul><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to &quot;参考资料&quot;">​</a></h2><ul><li><a href="https://www.cnblogs.com/tong-yuan/p/Heap.html" target="_blank" rel="noreferrer">拜托，面试别再问我堆（排序）了！</a></li></ul>',8),r=[o];function n(s,_,d,c,h,p){return a(),t("div",null,r)}const f=e(i,[["render",n]]);export{u as __pageData,f as default};
