import{_ as t,o as a,c as e,Q as r}from"./chunks/framework.419948d5.js";const f=JSON.parse('{"title":"分布式事务","description":"","frontmatter":{"title":"分布式事务","date":"2023-06-13T00:00:00.000Z","order":5},"headers":[],"relativePath":"architecture/distributed/transaction.md","filePath":"architecture/distributed/transaction.md","lastUpdated":1699298916000}'),o={name:"architecture/distributed/transaction.md"},i=r('<h1 id="分布式事务" tabindex="-1">分布式事务 <a class="header-anchor" href="#分布式事务" aria-label="Permalink to &quot;分布式事务&quot;">​</a></h1><p>事务是一个程序执行单元，里面的所有操作要么全部执行成功，要么全部执行失败。在分布式系统中，这些操作可能是位于不同的服务中，那么如果也能保证这些操作要么全部执行成功要么全部执行失败呢？这便是分布式事务要解决的问题。</p><h2 id="分布式事务中间件-seata" tabindex="-1">分布式事务中间件 Seata <a class="header-anchor" href="#分布式事务中间件-seata" aria-label="Permalink to &quot;分布式事务中间件 Seata&quot;">​</a></h2><blockquote><p>Seata 是一款开源的分布式事务解决方案，致力于在微服务架构下提供高性能和简单易用的分布式事务服务。</p><ul><li><a href="https://seata.io/zh-cn/index.html" target="_blank" rel="noreferrer">Seata 官网链接</a></li></ul></blockquote>',4),s=[i];function c(n,d,l,_,h,u){return a(),e("div",null,s)}const m=t(o,[["render",c]]);export{f as __pageData,m as default};
