import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as i,c as o,a as r,b as p,f as a}from"./app.fdaad276.js";const t={},n=p("p",null,"\u8BBE\u8BA1\u6A21\u5F0F\u57FA\u672C\u539F\u5219",-1),l=a('<h3 id="\u5355\u4E00\u804C\u8D23\u539F\u5219srp" tabindex="-1"><a class="header-anchor" href="#\u5355\u4E00\u804C\u8D23\u539F\u5219srp" aria-hidden="true">#</a> \u5355\u4E00\u804C\u8D23\u539F\u5219SRP</h3><blockquote><p>Single-Responsibility Principle\uFF0C\u5C31\u4E00\u4E2A\u7C7B\u800C\u8A00\uFF0C\u5E94\u8BE5\u4EC5\u6709\u4E00\u4E2A\u5F15\u8D77\u5B83\u53D8\u5316\u7684\u539F\u56E0\uFF0C\u901A\u4FD7\u7684\u8BF4\uFF0C\u5C31\u662F\u4E00\u4E2A\u7C7B\u53EA\u8D1F\u8D23\u4E00\u9879\u804C\u8D23\u3002\u6B64\u539F\u5219\u7684\u6838\u5FC3\u5C31\u662F<strong>\u89E3\u8026</strong>\u548C<strong>\u589E\u5F3A\u5185\u805A\u6027</strong>\u3002</p><p>\u7C7B\u7684\u804C\u8D23\u4E3B\u8981\u5305\u62EC\u4E24\u4E2A\u65B9\u9762: \u6570\u636E\u804C\u8D23\u548C\u884C\u4E3A\u804C\u8D23\uFF0C\u6570\u636E\u804C\u8D23\u901A\u8FC7\u5176\u5C5E\u6027\u6765\u4F53\u73B0\uFF0C\u800C\u884C\u4E3A\u804C\u8D23\u901A\u8FC7\u5176\u65B9\u6CD5\u6765\u4F53\u73B0\u3002</p><p>\u5982\u679C\u4E00\u4E2A\u7C7B\u627F\u62C5\u7684\u804C\u8D23\u8FC7\u591A\uFF0C\u5C31\u7B49\u4E8E\u628A\u8FD9\u4E9B\u804C\u8D23\u8026\u5408\u5728\u4E00\u8D77\uFF0C\u4E00\u4E2A\u804C\u8D23\u7684\u53D8\u5316\u53EF\u80FD\u4F1A\u524A\u5F31\u6216\u8005\u6291\u5236\u8FD9\u4E2A\u7C7B\u5B8C\u6210\u5176\u4ED6\u804C\u8D23\u7684\u80FD\u529B\u3002\u8FD9\u79CD\u8026\u5408\u4F1A\u5BFC\u81F4\u8106\u5F31\u7684\u8BBE\u8BA1\uFF0C\u53EF\u590D\u7528\u7684\u53EF\u80FD\u6027\u5C31\u8D8A\u5C0F\u3002</p><p>\u4E00\u4E2A\u7C7B\u53EA\u6709\u4E00\u4E2A\u5F15\u8D77\u5B83\u53D8\u5316\u7684\u539F\u56E0\uFF0C\u5426\u5219\u5C31\u5E94\u5F53\u8003\u8651\u91CD\u6784\uFF0C\u662F\u5426\u91CD\u6784\u7531\u5F15\u8D77\u53D8\u5316\u7684\u539F\u56E0\u51B3\u5B9A\uFF0C\u800C\u4E0D\u7531\u529F\u80FD\u51B3\u5B9A\u3002\u867D\u7136\u804C\u8D23\u5E38\u5E38\u5F15\u8D77\u53D8\u5316\u7684\u8F74\u7EBF\uFF0C\u4F46\u6709\u65F6\u5C31\u672A\u5FC5\uFF0C\u5E94\u8BE5\u9002\u5F53\u8003\u8651\uFF1B\u53EF\u4EE5\u901A\u8FC7Facade\u6A21\u5F0F\u6216Proxy\u6A21\u5F0F\u8FDB\u884C\u804C\u8D23\u5206\u79BB\u3002</p></blockquote><p>\u4F18\u70B9\uFF1A</p><ol><li><p>\u964D\u4F4E\u7C7B\u7684\u590D\u6742\u5EA6\uFF1B</p></li><li><p>\u63D0\u9AD8\u7C7B\u7684\u53EF\u8BFB\u6027\uFF0C\u63D0\u9AD8\u7CFB\u7EDF\u7684\u53EF\u7EF4\u62A4\u6027\uFF1B</p></li><li><p>\u964D\u4F4E\u53D8\u66F4\u5F15\u8D77\u7684\u98CE\u9669\uFF08\u964D\u4F4E\u5BF9\u5176\u4ED6\u529F\u80FD\u7684\u5F71\u54CD\uFF09\u3002</p></li></ol><p>\u4F8B\u5B50\uFF1A</p><p>SpringMVC \u4E2DEntity,DAO,Service,Controller, Util\u7B49\u7684\u5206\u79BB\u3002</p><h3 id="\u5F00\u95ED\u539F\u5219ocp" tabindex="-1"><a class="header-anchor" href="#\u5F00\u95ED\u539F\u5219ocp" aria-hidden="true">#</a> \u5F00\u95ED\u539F\u5219OCP</h3><blockquote><p>Open Close Principle\uFF0C\u5F00\u95ED\u539F\u5219\u5C31\u662F\u8BF4<strong>\u5BF9\u6269\u5C55\u5F00\u653E\uFF0C\u5BF9\u4FEE\u6539\u5173\u95ED</strong>\u3002\u5728\u7A0B\u5E8F\u9700\u8981\u8FDB\u884C\u62D3\u5C55\u7684\u65F6\u5019\uFF0C\u4E0D\u80FD\u53BB\u4FEE\u6539\u539F\u6709\u7684\u4EE3\u7801\uFF0C\u5B9E\u73B0\u4E00\u4E2A\u70ED\u63D2\u62D4\u7684\u6548\u679C\u3002\u6240\u4EE5\u4E00\u53E5\u8BDD\u6982\u62EC\u5C31\u662F\uFF1A\u4E3A\u4E86\u4F7F\u7A0B\u5E8F\u7684\u6269\u5C55\u6027\u597D\uFF0C\u6613\u4E8E\u7EF4\u62A4\u548C\u5347\u7EA7\u3002\u60F3\u8981\u8FBE\u5230\u8FD9\u6837\u7684\u6548\u679C\uFF0C\u6211\u4EEC\u9700\u8981\u4F7F\u7528<strong>\u63A5\u53E3</strong>\u548C<strong>\u62BD\u8C61\u7C7B</strong>\u3002</p></blockquote><p>\u4F8B\u5B50\uFF1A</p><p>\u8BBE\u8BA1\u6A21\u5F0F\u4E2D\u6A21\u677F\u65B9\u6CD5\u6A21\u5F0F\u548C\u89C2\u5BDF\u8005\u6A21\u5F0F\u3002</p><h3 id="\u91CC\u6C0F\u4EE3\u6362\u539F\u5219lsp" tabindex="-1"><a class="header-anchor" href="#\u91CC\u6C0F\u4EE3\u6362\u539F\u5219lsp" aria-hidden="true">#</a> \u91CC\u6C0F\u4EE3\u6362\u539F\u5219LSP</h3><blockquote><p>Liskov Substitution Principle\uFF0C\u6240\u6709\u5F15\u7528\u57FA\u7C7B(\u7236\u7C7B)\u7684\u5730\u65B9\u5FC5\u987B\u80FD\u900F\u660E\u5730\u4F7F\u7528\u5176\u5B50\u7C7B\u7684\u5BF9\u8C61\uFF0C\u5373\u5B50\u7C7B\u80FD\u591F\u5FC5\u987B\u80FD\u591F\u66FF\u6362\u57FA\u7C7B\u80FD\u591F\u4ECE\u51FA\u73B0\u7684\u5730\u65B9\uFF0C\u5B50\u7C7B\u4E5F\u80FD\u5728\u57FA\u7C7B\u7684\u57FA\u7840\u4E0A\u65B0\u589E\u884C\u4E3A\u3002</p><p>LSP\u662F\u7EE7\u627F\u590D\u7528\u7684\u57FA\u77F3\uFF0C\u662F\u5BF9\u201C\u5F00-\u95ED\u201D\u539F\u5219\u7684\u8865\u5145\u3002\u5B9E\u73B0\u201C\u5F00-\u95ED\u201D\u539F\u5219\u7684\u5173\u952E\u6B65\u9AA4\u5C31\u662F\u62BD\u8C61\u5316\uFF0C\u800C\u57FA\u7C7B\u4E0E\u5B50\u7C7B\u7684\u7EE7\u627F\u5173\u7CFB\u5C31\u662F\u62BD\u8C61\u5316\u7684\u5177\u4F53\u5B9E\u73B0\uFF0C\u6240\u4EE5\u91CC\u6C0F\u4EE3\u6362\u539F\u5219\u662F\u5BF9\u5B9E\u73B0\u62BD\u8C61\u5316\u7684\u5177\u4F53\u6B65\u9AA4\u7684\u89C4\u8303\u3002</p><p>\u91CC\u6C0F\u4EE3\u6362\u539F\u5219\u662F\u5B9E\u73B0\u5F00\u95ED\u539F\u5219\u7684\u91CD\u8981\u65B9\u5F0F\u4E4B\u4E00\uFF0C\u7531\u4E8E\u4F7F\u7528\u57FA\u7C7B\u5BF9\u8C61\u7684\u5730\u65B9\u90FD\u53EF\u4EE5\u4F7F\u7528\u5B50\u7C7B\u5BF9\u8C61\uFF0C<strong>\u56E0\u6B64\u5728\u7A0B\u5E8F\u4E2D\u5C3D\u91CF\u4F7F\u7528\u57FA\u7C7B\u7C7B\u578B\u6765\u5BF9\u5BF9\u8C61\u8FDB\u884C\u5B9A\u4E49\uFF0C\u800C\u5728\u8FD0\u884C\u65F6\u518D\u786E\u5B9A\u5176\u5B50\u7C7B\u7C7B\u578B\uFF0C\u7528\u5B50\u7C7B\u5BF9\u8C61\u6765\u66FF\u6362\u7236\u7C7B\u5BF9\u8C61</strong>\u3002</p></blockquote><p>\u4F8B\u5B50\uFF1A</p><p>\u6B63\u65B9\u5F62\u662F\u957F\u65B9\u5F62\u3002</p><h3 id="\u63A5\u53E3\u9694\u79BB\u539F\u5219isl" tabindex="-1"><a class="header-anchor" href="#\u63A5\u53E3\u9694\u79BB\u539F\u5219isl" aria-hidden="true">#</a> \u63A5\u53E3\u9694\u79BB\u539F\u5219ISL</h3><blockquote><p>(Interface Segregation Principle\uFF0CISL): \u5BA2\u6237\u7AEF\u4E0D\u5E94\u8BE5\u4F9D\u8D56\u90A3\u4E9B\u5B83\u4E0D\u9700\u8981\u7684\u63A5\u53E3\u3002(\u8FD9\u4E2A\u6CD5\u5219\u4E0E\u8FEA\u7C73\u7279\u6CD5\u5219\u662F\u76F8\u901A\u7684)\u3002</p><p>\u4E00\u65E6\u4E00\u4E2A\u63A5\u53E3\u592A\u5927\uFF0C\u5219\u9700\u8981\u5C06\u5B83\u5206\u5272\u6210\u4E00\u4E9B\u66F4\u7EC6\u5C0F\u7684\u63A5\u53E3\uFF0C\u4F7F\u7528\u8BE5\u63A5\u53E3\u7684\u5BA2\u6237\u7AEF\u4EC5\u9700\u77E5\u9053\u4E0E\u4E4B\u76F8\u5173\u7684\u65B9\u6CD5\u5373\u53EF\u3002 \u6CE8\u610F\uFF0C\u5728\u8BE5\u5B9A\u4E49\u4E2D\u7684\u63A5\u53E3\u6307\u7684\u662F\u6240\u5B9A\u4E49\u7684\u65B9\u6CD5\u3002\u4F8B\u5982\u5916\u9762\u8C03\u7528\u67D0\u4E2A\u7C7B\u7684public\u65B9\u6CD5\u3002\u8FD9\u4E2A\u65B9\u6CD5\u5BF9\u5916\u5C31\u662F\u63A5\u53E3\u3002</p></blockquote><h3 id="\u4F9D\u8D56\u5012\u8F6C\u539F\u5219dip" tabindex="-1"><a class="header-anchor" href="#\u4F9D\u8D56\u5012\u8F6C\u539F\u5219dip" aria-hidden="true">#</a> \u4F9D\u8D56\u5012\u8F6C\u539F\u5219DIP</h3><blockquote><p>Dependency-Inversion Principle \u8981\u4F9D\u8D56\u62BD\u8C61\uFF0C\u800C\u4E0D\u8981\u4F9D\u8D56\u5177\u4F53\u7684\u5B9E\u73B0\uFF0C\u5177\u4F53\u800C\u8A00\u5C31\u662F\u9AD8\u5C42\u6A21\u5757\u4E0D\u4F9D\u8D56\u4E8E\u5E95\u5C42\u6A21\u5757\uFF0C\u4E8C\u8005\u5171\u540C\u4F9D\u8D56\u4E8E\u62BD\u8C61\u3002\u62BD\u8C61\u4E0D\u4F9D\u8D56\u4E8E\u5177\u4F53\uFF0C\u5177\u4F53\u4F9D\u8D56\u4E8E\u62BD\u8C61\u3002</p><p>\u5B9E\u73B0\u5F00\u95ED\u539F\u5219\u7684\u5173\u952E\u662F\u62BD\u8C61\u5316\uFF0C\u5E76\u4E14\u4ECE\u62BD\u8C61\u5316\u5BFC\u51FA\u5177\u4F53\u5316\u5B9E\u73B0\uFF0C\u5982\u679C\u8BF4\u5F00\u95ED\u539F\u5219\u662F\u9762\u5411\u5BF9\u8C61\u8BBE\u8BA1\u7684\u76EE\u6807\u7684\u8BDD\uFF0C\u90A3\u4E48\u4F9D\u8D56\u5012\u8F6C\u539F\u5219\u5C31\u662F\u9762\u5411\u5BF9\u8C61\u8BBE\u8BA1\u7684\u4E3B\u8981\u624B\u6BB5\u3002</p></blockquote><h3 id="\u5176\u4ED6\u539F\u5219-\u5408\u6210\u590D\u7528\u539F\u5219" tabindex="-1"><a class="header-anchor" href="#\u5176\u4ED6\u539F\u5219-\u5408\u6210\u590D\u7528\u539F\u5219" aria-hidden="true">#</a> \u5176\u4ED6\u539F\u5219-\u5408\u6210\u590D\u7528\u539F\u5219</h3><blockquote><p>Composite Reuse Principle\uFF0C\u5408\u6210\u590D\u7528\u539F\u5219\u5C31\u662F\u6307\u5728\u4E00\u4E2A\u65B0\u7684\u5BF9\u8C61\u91CC\u901A\u8FC7\u5173\u8054\u5173\u7CFB\uFF08\u5305\u62EC\u7EC4\u5408\u5173\u7CFB\u548C\u805A\u5408\u5173\u7CFB\uFF09\u6765\u4F7F\u7528\u4E00\u4E9B\u5DF2\u6709\u7684\u5BF9\u8C61\uFF0C\u4F7F\u4E4B\u6210\u4E3A\u65B0\u5BF9\u8C61\u7684\u4E00\u90E8\u5206\uFF1B\u65B0\u5BF9\u8C61\u901A\u8FC7\u59D4\u6D3E\u8C03\u7528\u5DF2\u6709\u5BF9\u8C61\u7684\u65B9\u6CD5\u8FBE\u5230\u590D\u7528\u5176\u5DF2\u6709\u529F\u80FD\u7684\u76EE\u7684\u3002\u7B80\u8A00\u4E4B\uFF1A<strong>\u8981\u5C3D\u91CF\u4F7F\u7528\u7EC4\u5408/\u805A\u5408\u5173\u7CFB\uFF0C\u5C11\u7528\u7EE7\u627F</strong>\u3002</p></blockquote><h3 id="\u5176\u4ED6\u539F\u5219-\u8FEA\u7C73\u7279\u6CD5\u5219-\u6700\u5C11\u77E5\u9053\u539F\u5219" tabindex="-1"><a class="header-anchor" href="#\u5176\u4ED6\u539F\u5219-\u8FEA\u7C73\u7279\u6CD5\u5219-\u6700\u5C11\u77E5\u9053\u539F\u5219" aria-hidden="true">#</a> \u5176\u4ED6\u539F\u5219-\u8FEA\u7C73\u7279\u6CD5\u5219\uFF08\u6700\u5C11\u77E5\u9053\u539F\u5219\uFF09</h3><blockquote><p>Demeter Principle\uFF0C\u4E3A\u4EC0\u4E48\u53EB\u6700\u5C11\u77E5\u9053\u539F\u5219\uFF0C\u5C31\u662F\u8BF4\uFF1A\u4E00\u4E2A\u5B9E\u4F53\u5E94\u5F53\u5C3D\u91CF\u5C11\u7684\u4E0E\u5176\u4ED6\u5B9E\u4F53\u4E4B\u95F4\u53D1\u751F\u76F8\u4E92\u4F5C\u7528\uFF0C\u4F7F\u5F97\u7CFB\u7EDF\u529F\u80FD\u6A21\u5757\u76F8\u5BF9\u72EC\u7ACB\uFF0C\u8FD9\u6837\uFF0C\u5F53\u4E00\u4E2A\u6A21\u5757\u4FEE\u6539\u65F6\uFF0C\u5C31\u4F1A\u5C3D\u91CF\u5C11\u7684\u5F71\u54CD\u5176\u4ED6\u7684\u6A21\u5757\uFF0C\u6269\u5C55\u4F1A\u76F8\u5BF9\u5BB9\u6613\uFF0C\u8FD9\u662F\u5BF9\u8F6F\u4EF6\u5B9E\u4F53\u4E4B\u95F4\u901A\u4FE1\u7684\u9650\u5236\uFF0C\u5B83\u8981\u6C42\u9650\u5236\u8F6F\u4EF6\u5B9E\u4F53\u4E4B\u95F4\u901A\u4FE1\u7684\u5BBD\u5EA6\u548C\u6DF1\u5EA6\u3002</p><p>\u5728\u8FEA\u7C73\u7279\u6CD5\u5219\u4E2D\uFF0C\u5BF9\u4E8E\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u5176\u5173\u8054\u7684\u5B9E\u4F53\u5305\u62EC\u4EE5\u4E0B\u51E0\u7C7B:</p><ol><li>\u5F53\u524D\u5BF9\u8C61\u672C\u8EAB(this)\uFF1B</li><li>\u4EE5\u53C2\u6570\u5F62\u5F0F\u4F20\u5165\u5230\u5F53\u524D\u5BF9\u8C61\u65B9\u6CD5\u4E2D\u7684\u5BF9\u8C61\uFF1B</li><li>\u5F53\u524D\u5BF9\u8C61\u7684\u6210\u5458\u5BF9\u8C61\uFF08\u5305\u62EC\u96C6\u5408\u4E2D\u7684\u5143\u7D20\uFF09\uFF1B</li><li>\u5F53\u524D\u5BF9\u8C61\u6240\u521B\u5EFA\u7684\u5BF9\u8C61\u3002</li></ol></blockquote><p>\u4F8B\u5B50\uFF1A</p><p>\u5916\u89C2\u6A21\u5F0FFacade(\u7ED3\u6784\u578B)</p>',24);function c(s,d){return i(),o("div",null,[n,r(" more "),l])}const b=e(t,[["render",c],["__file","principle.html.vue"]]);export{b as default};
