import{_ as i,o as l,c as e,Q as t}from"./chunks/framework.419948d5.js";const m=JSON.parse('{"title":"Spring SpEl 表达式语言","description":"","frontmatter":{"title":"Spring SpEl 表达式语言","article":true,"date":"2022-10-21T00:00:00.000Z","tag":"SpEl"},"headers":[],"relativePath":"spring/spring/2.7-spel.md","filePath":"spring/spring/2.7-spel.md","lastUpdated":1697593879000}'),p={name:"spring/spring/2.7-spel.md"},s=t('<h2 id="spring-的表达式语言-spel" tabindex="-1">Spring 的表达式语言 (SpEL) <a class="header-anchor" href="#spring-的表达式语言-spel" aria-label="Permalink to &quot;Spring 的表达式语言 (SpEL)&quot;">​</a></h2><p>Spring Expression Language (简称 &quot;SpEL&quot;) 是一种强大的表达式语言，支持在运行时查询和操作对象。语言语法类似于 Unified EL，但提供了其他功能，最有名的是方法调用和基本字符串模板功能。</p><p>表达式语言支持以下功能：</p><ul><li>文字表达</li><li>布尔和关系运算符</li><li>正则表达式</li><li>类表达式</li><li>访问属性，数组，list 和 maps</li><li>方法调用</li><li>关系运算符</li><li>Assignment</li><li>调用构造器</li><li>bean 的引用</li><li>数组的构造</li><li>内嵌的 list</li><li>内嵌的 map</li><li>三元表达式</li><li>变量</li><li>用户自定义函数</li><li>集合映射</li><li>集合选择</li><li>模板表达式</li></ul>',4),a=[s];function n(r,o,_,c,d,g){return l(),e("div",null,a)}const u=i(p,[["render",n]]);export{m as __pageData,u as default};
