import{_ as e,o as r,c as i,k as t,a as s}from"./chunks/framework.419948d5.js";const m=JSON.parse('{"title":"Spring Security OAuth","description":"","frontmatter":{"title":"Spring Security OAuth","date":"2023-06-07T00:00:00.000Z"},"headers":[],"relativePath":"spring/springsecurity/1.1-spring-security-oauth.md","filePath":"spring/springsecurity/1.1-spring-security-oauth.md","lastUpdated":1697504454000}'),n={name:"spring/springsecurity/1.1-spring-security-oauth.md"},u=t("h2",{id:"什么是-spring-security-oauth",tabindex:"-1"},[s("什么是 Spring Security OAuth？ "),t("a",{class:"header-anchor",href:"#什么是-spring-security-oauth","aria-label":'Permalink to "什么是 Spring Security OAuth？"'},"​")],-1),a=t("p",null,"Spring Security OAuth 是一个基于 Spring Security 的开源框架，用于实现 OAuth 2.0 协议中的授权服务器和资源服务器。OAuth（开放授权）是一种用于授权第三方应用程序访问受保护资源的开放标准。",-1),c=t("p",null,"使用 Spring Security OAuth，您可以轻松地将 OAuth 2.0 集成到您的应用程序中，以提供身份验证和授权机制。它提供了一组 API 和工具，使您能够快速构建安全的 OAuth 2.0 服务。它支持多种授权模式，包括授权码模式、密码模式、客户端模式和刷新令牌模式，并且可以与各种身份提供者和认证服务器集成，如 Google、Facebook、GitHub 等。",-1),o=t("p",null,"Spring Security OAuth 还提供了一些常见的扩展功能，如令牌存储、令牌刷新、令牌撤销、令牌自定义等，以满足更复杂的授权需求。",-1),p=t("p",null,"总的来说，Spring Security OAuth 提供了一种简单而强大的方式来实现 OAuth 2.0 授权服务和资源保护，帮助开发人员构建安全可靠的应用程序。",-1),h=[u,a,c,o,p];function _(d,l,g,S,y,O){return r(),i("div",null,h)}const f=e(n,[["render",_]]);export{m as __pageData,f as default};
