import{_ as r,o as e,c as t,Q as i}from"./chunks/framework.bf9fbf22.js";const d=JSON.parse('{"title":"Spring Authorization Server","description":"","frontmatter":{"title":"Spring Authorization Server","date":"2023-06-07T00:00:00.000Z"},"headers":[],"relativePath":"spring/springsecurity/1.2-spring-authorization-server.md","filePath":"spring/springsecurity/1.2-spring-authorization-server.md"}'),n={name:"spring/springsecurity/1.2-spring-authorization-server.md"},o=i('<h2 id="什么是-spring-authorization-server" tabindex="-1">什么是 Spring Authorization Server？ <a class="header-anchor" href="#什么是-spring-authorization-server" aria-label="Permalink to &quot;什么是 Spring Authorization Server？&quot;">​</a></h2><p><strong>Spring Authorization Server</strong> 是 Spring 团队最新开发适配 OAuth 协议的授权服务器项目，旨在替代原有的 Spring Security OAuth。</p><p>Spring Authorization Server 提供 OAuth 2.1 和 OpenID Connect 1.0 规范以及其他相关规范的实现。</p><p>它建立在 <code>Spring Security</code> 之上，为构建 OpenID Connect 1.0 身份提供程序和 OAuth2 授权服务器产品提供了一个安全、轻量级和可定制的基础。</p><p>其完整的功能列表参见 <a href="https://docs.spring.io/spring-authorization-server/docs/0.4.2/reference/html/overview.html" target="_blank" rel="noreferrer">feature-list</a></p><p>值得一提的是，Spring Authorization Server 支持授权码、客户端、刷新、注销等 OAuth 协议，但是<strong>不支持密码模式</strong>，需要自己实现。</p>',6),a=[o];function s(p,h,c,u,g,_){return e(),t("div",null,a)}const l=r(n,[["render",s]]);export{d as __pageData,l as default};
