import{_ as r,o as e,c as t,Q as i}from"./chunks/framework.01af844e.js";const d=JSON.parse('{"title":"Spring Security 简介","description":"","frontmatter":{"title":"Spring Security 简介","date":"2023-11-28T00:00:00.000Z","order":1},"headers":[],"relativePath":"spring/springsecurity/concept.md","filePath":"spring/springsecurity/concept.md","lastUpdated":1713403090000}'),n={name:"spring/springsecurity/concept.md"},a=i('<h1 id="spring-security-简介" tabindex="-1">Spring Security 简介 <a class="header-anchor" href="#spring-security-简介" aria-label="Permalink to &quot;Spring Security 简介&quot;">​</a></h1><h2 id="什么是-springsecurity" tabindex="-1">什么是 SpringSecurity？ <a class="header-anchor" href="#什么是-springsecurity" aria-label="Permalink to &quot;什么是 SpringSecurity？&quot;">​</a></h2><p>Spring Security 是一个提供<strong>身份验证</strong>、<strong>授权</strong>和<strong>针对常见攻击的保护</strong>的框架。凭借对保护 Servlet 和 reactive 应用程序的一流支持。</p><p>开始入门 Spring Security 可以参考如下资料：</p><ul><li><a href="./1.0-spring-security.html">Spring Security 入门</a></li><li><a href="https://docs.spring.io/spring-security/reference/5.7/index.html" target="_blank" rel="noreferrer">Spring Security 官方文档</a></li></ul><h2 id="oauth2-协议支持" tabindex="-1">OAuth2 协议支持 <a class="header-anchor" href="#oauth2-协议支持" aria-label="Permalink to &quot;OAuth2 协议支持&quot;">​</a></h2><p>OAuth（Open Authorization）是一个关于授权（authorization）的开放网络标准，允许用户授权第三方应用访问他们存储在另外的服务提供者上的信息，而不需要将用户名和密码提供给第三方移动应用或分享他们数据的所有内容。OAuth 在全世界得到广泛应用，目前的版本是 2.0 版。了解更多可以查看 <a href="./oauth2.html">理解 OAuth2</a>。</p><p>SpringSecurity 提供了一系列对 OAuth2 协议的实现，可以分为授权服务器、资源服务器和授权客户端三个角色，并提供了 SpringBoot starter 的自动配置支持。</p><p>在 Spring Security 6.0 时，Spring 团队将授权服务器单独抽离出来开发，名为 <code>Spring Authorization Server</code>，其余两个仍然在 Spring Security 中。值得一提的是，Spring Authorization Server 支持授权码、客户端、刷新、注销等 OAuth 协议，但是<strong>不支持密码模式</strong>，需要自己实现。</p><p>相关参考资料：</p><ul><li><a href="https://docs.spring.io/spring-security/reference/servlet/oauth2/index.html" target="_blank" rel="noreferrer">Spring Security - OAuth2 ｜ 官网</a></li><li><a href="https://springdoc.cn/spring-security/servlet/oauth2/index.html" target="_blank" rel="noreferrer">Spring Security - OAuth2 ｜ 中文翻译</a></li><li><a href="https://docs.spring.io/spring-authorization-server/reference/overview.html" target="_blank" rel="noreferrer">授权服务器 - 官网</a></li><li><a href="https://springdoc.cn/spring-authorization-server/overview.html" target="_blank" rel="noreferrer">授权服务器 - 中文翻译</a></li></ul>',11),o=[a];function s(p,c,u,h,l,g){return e(),t("div",null,o)}const _=r(n,[["render",s]]);export{d as __pageData,_ as default};
